import React, { useState, useEffect } from 'react';
import './ImageSlider.css'; // Import your CSS file for styling

const images = [
  {

    imgPath:
      'data:image/jpeg;base64,',
  },
  {
    
    imgPath:
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSWTNwmcarkhrl5-1ZQJrwJ2iqx9l4bl6f9Cg&s',
  },
  {
  
    imgPath:
      'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSjh3Wabz8vVw320xg2h-iILdgGGoqar4QX1w&s',
  },
  {
   
    imgPath:
      'data:image/jpeg;base64,',
  },
];

const ImageSlider= () => {
  const [activeStep, setActiveStep] = useState(0);
  const maxSteps = images.length;
  let autoPlayTimer;

  useEffect(() => {
    
    autoPlayTimer = setTimeout(() => {
      const nextStep = (activeStep + 1) % maxSteps;
      setActiveStep(nextStep);
    }, 2000);

    return () => clearTimeout(autoPlayTimer); 
  }, [activeStep]); 

  const handleNext = () => {
    setActiveStep((prevActiveStep) => (prevActiveStep + 1) % maxSteps);
    resetTimer();
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => (prevActiveStep - 1 + maxSteps) % maxSteps);
    resetTimer();
  };

  const resetTimer = () => {
    clearTimeout(autoPlayTimer);
    autoPlayTimer = setTimeout(() => {
      const nextStep = (activeStep + 1) % maxSteps;
      setActiveStep(nextStep);
    }, 3000);
  };

  return (
    <div className="carousel-container" style={{padding:'100px'}}>
      <div className="carousel">
        <div className="carousel-inner">
          {images.map((image, index) => (
            <div
              key={image.label}
              className={`carousel-item ${index === activeStep ? 'active' : ''}`}
            >
              <img
                src={image.imgPath}
                alt={image.label}
                className="carousel-image"
              />
              <div className="carousel-caption">
                <h3>{image.label}</h3>
                {/* Optional description */}
                {/* <p>Description here</p> */}
              </div>
            </div>
          ))}
        </div>
        <button className="carousel-control-prev" onClick={handleBack}>
          &lsaquo;
        </button>
        <button className="carousel-control-next" onClick={handleNext}>
          &rsaquo;
        </button>
      </div>
    </div>
  );
};

export default ImageSlider;
